// Type annotation
let name: string = "Alice";

// Type alias
type ID = string | number;

// Interface
interface User { name: string; }

// Union (OR)
type Result = string | number;

// Intersection (AND)
type Combined = TypeA & TypeB;

// Optional property
interface Config { timeout?: number; }

// Readonly property
interface Point { readonly x: number; }

// Generic
function identity<T>(arg: T): T { return arg; }

// Type assertion
let value = someValue as string;

// Non-null assertion
let name = user!.name;

// Type guard
if (typeof value === "string") { }

// Keyof (keys of object)
type Keys = keyof User;

// Typeof (get type)
const obj = { a: 1 };
type ObjType = typeof obj;

// Indexed access
type Age = User["age"];

// Conditional type
type Check<T> = T extends string ? true : false;

// Infer type
type Flatten<T> = T extends (infer U)[] ? U : T;

// Template literal
type EventName = `on${string}`;

// Make all properties optional
Partial<User>

// Make all properties required
Required<User>

// Make all properties readonly
Readonly<User>

// Pick specific properties
Pick<User, "name" | "email">

// Omit specific properties
Omit<User, "password">

// Create object type with keys
Record<string, number>

// Extract function return type
ReturnType<typeof func>

// Extract function parameters
Parameters<typeof func>

// Remove types from union
Exclude<T, U>

// Keep only types from union
Extract<T, U>

// Remove null and undefined
NonNullable<T>

// Awaited type from Promise
Awaited<Promise<string>> // string

