examples.code:
// hello.ts
const greeting: string = 'Hello, World!';
console.log(greeting);

// Compile: tsc hello.ts
// Run: node hello.js
// Output: Hello, World!

// math.ts
function add(a: number, b: number): number {
  return a + b;
}

const result = add(5, 3);
console.log(result); // 8

// This would cause a compile error:
// add('5', '3'); // Error: Argument of type 'string' is not assignable to parameter of type 'number'

// user.ts
interface User {
  name: string;
  age: number;
  email: string;
}

const user: User = {
  name: 'Alice',
  age: 30,
  email: 'alice@example.com'
};

console.log(`${user.name} is ${user.age} years old`);

// arrays.ts
const numbers: number[] = [1, 2, 3, 4, 5];
const names: string[] = ['Alice', 'Bob', 'Charlie'];

// Array methods are type-safe
const doubled = numbers.map(n => n * 2);
console.log(doubled); // [2, 4, 6, 8, 10]

// TypeScript infers the callback parameter types
names.forEach(name => {
  console.log(name.toUpperCase()); // 'name' is known to be string
});

// inference.ts
// TypeScript infers 'message' is a string
let message = 'Hello';

// TypeScript infers 'count' is a number
let count = 42;

// TypeScript infers the return type is string
function getGreeting(name: string) {
  return `Hello, ${name}!`; // Return type inferred as string
}

const greeting = getGreeting('World');
console.log(greeting); // Hello, World!

// package.json (scripts section)
{
  "scripts": {
    "build": "tsc",
    "start": "node dist/index.js",
    "dev": "tsc --watch",
    "clean": "rm -rf dist",
    "build:start": "npm run build && npm start"
  }
}

// Usage:
// npm run build    - Compile TypeScript
// npm start        - Run compiled code
// npm run dev      - Watch mode for development
