// tsconfig.json - Start with this for any new project
{
  "compilerOptions": {
    // Language Settings
    "target": "ES2020",
    "lib": ["ES2020"],

    // Modules
    "module": "commonjs",
    "moduleResolution": "node",
    "esModuleInterop": true,

    // Strictness - ALWAYS enable for new projects
    "strict": true,

    // Performance
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true
  }
}

// tsconfig.json for React + Vite projects
{
  "compilerOptions": {
    // Modern JavaScript for browsers
    "target": "ES2020",
    "lib": ["ES2020", "DOM", "DOM.Iterable"],
    "useDefineForClassFields": true,

    // ESM modules for tree-shaking
    "module": "ESNext",
    "moduleResolution": "bundler",

    // React JSX transform
    "jsx": "react-jsx",

    // Strictness
    "strict": true,
    "noUnusedLocals": true,
    "noUnusedParameters": true,
    "noFallthroughCasesInSwitch": true,

    // Don't emit - Vite handles compilation
    "noEmit": true,
    "isolatedModules": true,

    // Interop
    "allowSyntheticDefaultImports": true,
    "esModuleInterop": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true,

    // Path aliases
    "baseUrl": ".",
    "paths": {
      "@/*": ["src/*"],
      "@components/*": ["src/components/*"],
      "@hooks/*": ["src/hooks/*"],
      "@utils/*": ["src/utils/*"]
    },

    // JSON imports
    "resolveJsonModule": true
  },
  "include": ["src"],
  "references": [{ "path": "./tsconfig.node.json" }]
}

// tsconfig.node.json for Vite config
{
  "compilerOptions": {
    "composite": true,
    "module": "ESNext",
    "moduleResolution": "bundler",
    "allowSyntheticDefaultImports": true
  },
  "include": ["vite.config.ts"]
}

// tsconfig.json for Node.js backend
{
  "compilerOptions": {
    // Node.js 18+ target
    "target": "ES2022",
    "lib": ["ES2022"],

    // Node.js ESM support
    "module": "NodeNext",
    "moduleResolution": "NodeNext",

    // Output configuration
    "outDir": "./dist",
    "rootDir": "./src",

    // Strictness
    "strict": true,
    "noImplicitReturns": true,
    "noFallthroughCasesInSwitch": true,
    "noUnusedLocals": true,
    "noUnusedParameters": true,

    // Debugging
    "sourceMap": true,
    "declaration": true,
    "declarationMap": true,

    // Interop
    "esModuleInterop": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true,

    // Path aliases
    "baseUrl": "./src",
    "paths": {
      "@/*": ["./*"],
      "@controllers/*": ["controllers/*"],
      "@services/*": ["services/*"],
      "@models/*": ["models/*"],
      "@middleware/*": ["middleware/*"],
      "@utils/*": ["utils/*"]
    },

    // JSON imports
    "resolveJsonModule": true
  },
  "include": ["src/**/*"],
  "exclude": [
    "node_modules",
    "dist",
    "**/*.test.ts",
    "**/*.spec.ts"
  ]
}

// tsconfig.json for publishing an npm package
{
  "compilerOptions": {
    // Wide compatibility
    "target": "ES2018",
    "lib": ["ES2018"],

    // Dual CJS/ESM output (use tsup or unbuild for actual bundling)
    "module": "ESNext",
    "moduleResolution": "Node",

    // Output
    "outDir": "./dist",
    "rootDir": "./src",

    // Type declarations - REQUIRED for libraries
    "declaration": true,
    "declarationDir": "./dist/types",
    "declarationMap": true,
    "sourceMap": true,

    // Strictness - libraries should be strict
    "strict": true,
    "noImplicitReturns": true,
    "noFallthroughCasesInSwitch": true,

    // Optimization
    "removeComments": true,
    "importHelpers": true,

    // Interop
    "esModuleInterop": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true
  },
  "include": ["src/**/*"],
  "exclude": [
    "node_modules",
    "dist",
    "**/*.test.ts",
    "**/*.spec.ts",
    "**/__tests__/**"
  ]
}

