// tsconfig.json - Base config
{
  "compilerOptions": {
    "target": "ES2020",
    "lib": ["ES2020", "DOM"],
    "module": "ESNext",
    "moduleResolution": "bundler",
    "strict": true,
    "esModuleInterop": true,
    "skipLibCheck": true,
    "outDir": "./dist",
    "rootDir": "./src"
  },
  "include": ["src/**/*"],
  "exclude": ["**/*.test.ts", "**/*.spec.ts"]
}

// tsconfig.test.json - Extended config for tests
{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    // Include DOM for testing-library
    "lib": ["ES2020", "DOM", "DOM.Iterable"],

    // Tests don't need to be strict about unused vars
    "noUnusedLocals": false,
    "noUnusedParameters": false,

    // Allow test-specific patterns
    "types": ["jest", "@testing-library/jest-dom"]
  },
  "include": [
    "src/**/*",
    "tests/**/*",
    "**/*.test.ts",
    "**/*.spec.ts"
  ]
}

