// tsconfig.json for Next.js
{
  "compilerOptions": {
    // Next.js handles the target
    "target": "ES2017",
    "lib": ["DOM", "DOM.Iterable", "ESNext"],

    // Next.js module settings
    "module": "ESNext",
    "moduleResolution": "bundler",
    "allowJs": true,

    // JSX
    "jsx": "preserve",

    // Strictness
    "strict": true,
    "noEmit": true,
    "incremental": true,

    // Interop
    "esModuleInterop": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true,
    "resolveJsonModule": true,
    "isolatedModules": true,

    // Path aliases (must match next.config.js)
    "baseUrl": ".",
    "paths": {
      "@/*": ["src/*"],
      "@/components/*": ["src/components/*"],
      "@/lib/*": ["src/lib/*"],
      "@/styles/*": ["src/styles/*"]
    },

    // Next.js plugin handles JSX
    "plugins": [
      { "name": "next" }
    ]
  },
  "include": [
    "next-env.d.ts",
    "**/*.ts",
    "**/*.tsx",
    ".next/types/**/*.ts"
  ],
  "exclude": ["node_modules"]
}
